import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import styles from "./App.module.scss";

interface IpAddress {
  ip: string;
  location: {
    country: string;
    region: string;
    timezone: string;
  };
  as: {
    asn: number;
    domain: string;
    name: string;
    route: string;
    type: string;
  }
}

function App() {
  const [ipAddress, setIpAddress] = useState<IpAddress | null>(null);
  useEffect(() => {
    fetch("https://api.ipify.org?format=json&callback=?")
      .then((response) => response.json())
      .then((data) => setIpAddress(data));
  }, []);
  useEffect(() => {
    if (ipAddress) {
      fetch(
        `https://geo.ipify.org/api/v2/country?apiKey=at_3qmlDaay8nIBIyxf7Lia1IPzdJ3X3&ipAddress=${ipAddress.ip}`
      )
        .then((response) => response.json())
        .then((data) => console.log(data, "my ip ifo"));
    }
  }, [ipAddress]);
  return (
    <div className={styles.App}>
      {ipAddress ? <div className={styles.info}>{ipAddress.ip} {ipAddress.location?.country}</div> : <div>No info</div>}
    </div>
  );
}

export default App;
